"use strict";

const init = require('../utils/init');

exports.command = 'init';
exports.describe = 'Adds all required XDN dependencies and files to your app.';
exports.builder = {
  xdnVersion: {
    type: 'string',
    describe: 'The XDN version to install.',
    default: 'latest'
  },
  skipXdnDeps: {
    type: 'boolean',
    describe: 'If true, XDN packages will not be installed.'
  },
  connector: {
    type: 'string',
    describe: 'The name of a connector package to install and use to prepare your app for deployment on the XDN.'
  }
};

exports.handler = async ({
  context,
  xdnVersion,
  skipXdnDeps,
  connector
}) => {
  await init({
    context,
    version: xdnVersion,
    skipXdnDeps,
    connector
  });
};