"use strict";

const slash = require('slash');

const {
  join
} = require('path');

const {
  existsSync
} = require('fs');

module.exports = function getXdnConfig() {
  const file = join(process.cwd(), 'xdn.config.js');

  if (existsSync(file)) {
    return require(slash(file));
  } else {
    return {};
  }
};